mapper.select:
select * from tb_room r join tb_roomType rt on r.roomType_id = rt.roomType_id join tb_street s on rt.street_id = s.street_id where 1=1
and room_name like CONCAT('%', #{room_name}, '%')
and street_name=#{street_name} and roomType_name=#{roomType_name} order by room_name limit #{startIndex} , #{pageSize}

select count(*) from tb_room r join tb_roomType rt on r.roomType_id = rt.roomType_id join tb_street s on rt.street_id = s.street_id where 1=1
and room_name like CONCAT('%', #{room_name}, '%')
and street_name=#{street_name} and roomType_name=#{roomType_name}

select * from tb_room r join tb_roomType rt on r.roomType_id = rt.roomType_id join tb_street s on rt.street_id = s.street_id where 1=1
and room_name like CONCAT('%', #{room_name}, '%')
and street_name=#{street_name} and isRent=#{isRent} order by room_name limit #{startIndex} , #{pageSize}

select count(*) from tb_room r join tb_roomType rt on r.roomType_id = rt.roomType_id join tb_street s on rt.street_id = s.street_id where 1=1
and room_name like CONCAT('%', #{room_name}, '%')
and street_name=#{street_name} and isRent=#{isRent}

select * from tb_room r join tb_tenant t on t.room_id = r.room_id join tb_roomType rt on r.roomType_id = rt.roomType_id join tb_street s on rt.street_id = s.street_id where 1=1
and room_name like CONCAT('%', #{room_name}, '%')
and street_name=#{street_name} and isRent=#{isRent} and DATE_FORMAT(contract_term_start, '%m') = DATE_FORMAT(NOW(), '%m')-3 order by room_name limit #{startIndex} , #{pageSize}

mapper.update:
update tb_room 
    set isRent = #{isRent}
      where room_id = #{room_id}
